package interview.Strings;

/**
 * @author rajasan
 */
public class RepeatedSubStringPattern {

    public boolean repeatedSubstringPattern(String s) {

        if (s == null || s.isEmpty()) {
            return false;
        }

        for (int i = 0; i <= s.length() / 2; i++) {
            if (repeatedStringMatch(s.substring(0, i), s)) {
                return true;
            }
        }

        return false;
    }

    public boolean repeatedStringMatch(String A, String B) {

        if (A == null || B == null || A.isEmpty() || B.isEmpty()) {
            return false;
        }

        StringBuffer buffer = new StringBuffer();

        while (buffer.length() < B.length()) {
            buffer.append(A);
        }

        if (buffer.toString().equals(B)) {
            return true;
        } else if (buffer.append(A).toString().equals(B)) {
            return true;
        }

        return false;
    }

    public boolean repeatedSubstringPattern1(String str) {
        String s = str + str;
        return s.substring(1, s.length() - 1).contains(str);
    }

    public static void main(String[] args) {
        RepeatedSubStringPattern r = new RepeatedSubStringPattern();
        System.out.println(r.repeatedSubstringPattern1("abab"));
        System.out.println(r.repeatedSubstringPattern1("aabaaba"));
        System.out.println(r.repeatedSubstringPattern1("abcabcabcabc"));
        long start_time = System.nanoTime();

        System.out.println(r.repeatedSubstringPattern1(
                ""));
        long end_time = System.nanoTime();

        System.out.println((end_time - start_time) / 1e6);
    }

}
